const items = $input.all();

return items.map(item => {
  // Sicherstellen, dass wir einen String haben (manchmal kommt es als Objekt an)
  let rawText = typeof item.json.text === 'object' 
                ? JSON.stringify(item.json.text) 
                : item.json.text;

  // Wenn kein Text da ist, Fehler ausgeben
  if (!rawText) {
      return { json: { error: "Input is empty" } };
  }

  try {
    // SCHRITT 1: Versuch, direkt zu parsen (falls es schon sauber ist)
    const parsedData = JSON.parse(rawText);
    return { json: parsedData };

  } catch (e1) {
    // SCHRITT 2: Wenn direktes Parsen fehlschlägt -> Bereinigen (Extraction)
    try {
      // Finde den Index des ersten '{' oder '['
      const firstOpen = rawText.search(/[{[]/);

      // Finde den Index des LETZTEN '}' oder ']'
      const lastCloseBrace = rawText.lastIndexOf('}');
      const lastCloseBracket = rawText.lastIndexOf(']');
      const lastClose = Math.max(lastCloseBrace, lastCloseBracket);

      // Wenn wir gültige Start- und Endpunkte haben
      if (firstOpen !== -1 && lastClose !== -1 && lastClose > firstOpen) {

        // Schneide alles aus, was zwischen der ersten und letzten Klammer steht
        const cleanJson = rawText.substring(firstOpen, lastClose + 1);

        const parsedData = JSON.parse(cleanJson);
        return { json: parsedData };

      } else {
        throw new Error("No JSON brackets found");
      }

    } catch (e2) {
      // Wenn auch das Bereinigen fehlschlägt, gib das Original zur Analyse zurück
      return {
        json: {
          error: "Parsing failed",
          message: e2.message,
          original_text: rawText
        }
      };
    }
  }
});

const items = $input.all();

return items.map(item => {
  const data = item.json;

  // 1. Header & Zusammenfassung
  let emailBody = `<h2>Zusammenfassung</h2>`;
  emailBody += `<p>${data.summary}</p>`;
  emailBody += `<hr>`;

  // 2. KI-Antworten (Wissen)
  if (data.ki_antworten && data.ki_antworten.length > 0) {
    emailBody += `<h2>Antworten auf deine Fragen</h2>`;
    data.ki_antworten.forEach(qa => {
      emailBody += `<p><strong>F: ${qa.frage}</strong><br>`;
      emailBody += `A: ${qa.antwort}</p>`;
    });
    emailBody += `<hr>`;
  }
  if (data.tasks && data.tasks.length > 0) {
    emailBody += `<h2>Erfasste Aufgaben</h2><ul>`;
    data.tasks.forEach(t => {
      emailBody += `<li>[${t.Kategorie}] <strong>${t.task}</strong> (Status: ${t.Status})</li>`;
    });
    emailBody += `</ul>`;
  }

  // Rückgabe an den nächsten Node (z.B. Gmail oder Outlook)
  return {
    json: {
      emailSubject: `Protokoll: ${data.summary.substring(0, 40)}...`,
      emailHtml: emailBody,
      kategorie: data.tasks[0]?.Kategorie || "GENERAL"
    }
  };
});
